/* jquery linenumbers v1.0.2 - 09/17/2017 */
(function($){ $.fn.linenumbers = function(in_opts){  var opt = $.extend({ col_width: '65px', start: 1, digits: 3. },in_opts);  $("[data-name='linenumbers']").remove();  return this.each(function(){  var textarea_width = $(this).prop("offsetWidth"); var textarea_height = $(this).prop("offsetHeight"); var new_textarea_width = parseInt(textarea_width)-parseInt(opt.col_width);  $(this).before('<textarea data-name="linenumbers" style="width:'+new_textarea_width+'px;height:'+textarea_height+'px;float:left;margin-right:'+'-'+new_textarea_width+'px;white-space:pre;overflow:hidden;" disabled="disabled"></textarea>');  $(this).css({'width':new_textarea_width+'px','height':textarea_height+'px','float':'right'});  var lnbox = $(this).parent().find('textarea[disabled="disabled"]');  $(this).bind('blur focus change keyup keydown',function(){  var lines = "\n"+$(this).val(); lines = lines.match(/[^\n]*\n[^\n]*/gi);  var line_number_output='';  var max_spacers = ''; var spacers = ''; for(i=0;i<opt.digits;i++){ max_spacers += ' '; }  $.each(lines,function(k,v){  if(k!=0){ line_number_output += "\n"; }  lencheck = k+opt.start+'!'; spacers = max_spacers.substr(lencheck.length-1);  line_number_output += spacers+(k+opt.start)+':'; });  $(lnbox).val(line_number_output);  $(lnbox).scrollTop($(this).scrollTop()); });  $(this).scroll(function(){ $(lnbox).scrollTop($(this).scrollTop()); });  $(this).trigger('keyup'); }); };})(jQuery);$('textarea').linenumbers();